import React, { Fragment, useEffect } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { List, ListItem, ListItemIcon, ListItemText, Divider, Icon, Typography, TextField } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
        maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
    },
    listItem: {
        display: 'block'
    },
    secondary: {
        display: 'block',
        margin: '10px 0px',
    },
}));

function About(props) {

    const classes = useStyles();

    const [loaded, setLoaded] = React.useState(false);
    const [contactInfo, setContactInfo] = React.useState(true);
    const [educationInfo, setEducationInfo] = React.useState(false);
    const [editInfo, setEditInfo] = React.useState(false);
    const [education, setEducation] = React.useState('');
    const [selfTaught, setSelfTaught] = React.useState('');
    const [skills, setSkills] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [phone, setPhone] = React.useState('');
    const [linkedIn, setLinkedIn] = React.useState('');

    useEffect(() => {
        if((!loaded) && (props.auth)){
            setLoaded(true); 
            setEducation(props.auth.education.education)
            setSelfTaught(props.auth.education.selfTaught)
            setSkills(props.auth.education.skills)
            setEmail(props.auth.contact.email)
            setPhone(props.auth.contact.phone)
            setLinkedIn(props.auth.contact.linkedIn)
        }
    })


    function renderEducation() {
        console.log(props);
        return (
            <List>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Education" />
                    <Divider />
                        {editInfo ? renderAbout(education) : 

                            <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                                {props.info.education.education}
                            </Typography>
                            }
                </ListItem>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Self Taught" />
                    <Divider />
                    {editInfo ? renderAbout(selfTaught) : 

                        <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                            {props.info.education.selfTaught}
                        </Typography>
                        }
                </ListItem>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Skills" />
                    <Divider />
                    {editInfo ? renderAbout(skills) : 

                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {props.info.education.skills}
                    </Typography>
                        }
                </ListItem>
            </List>
        )
    }

    function renderContact() {
        return (
            <List>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Email" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {props.info.contact ? props.info.contact.email : ''}
                    </Typography>
                </ListItem>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="Phone" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        {props.info.contact ? props.info.contact.phone : ''}
                    </Typography>
                </ListItem>
                <ListItem className={classes.listItem}>
                    <ListItemText primary="LinkedIn" />
                    <Divider />
                    <Typography className={classes.secondary} component="p" variant="body2" color="textPrimary">
                        { props.info.contact ? <a href={props.info.contact.linkedIn} target="_blank">{props.info.contact.linkedIn}</a> : ''}
                    </Typography>
                </ListItem>
            </List>
        ); 
    }

    function renderList() {
        if(contactInfo){return renderContact()};
        if(educationInfo){return renderEducation()};
    }

    function renderEdit() {
        console.log(editInfo)
        return (
            <ListItem button onClick={() => setEditInfo(!editInfo)}>
                <ListItemIcon>
                    <Icon>edit</Icon>
                </ListItemIcon>
                <ListItemText primary="Edit Info" />
            </ListItem> 
        );
    }

    function renderAbout(section) {
        console.log(section)
        return (
            <TextField 
                value={section}
                onChange={handleChange}
                fullWidth
                multiline
            />
        )
    }

    function handleChange() {

    }

    function handleClick(item) {
        console.log(item)
        switch (item) {
            case 'contact':
                setContactInfo(true);
                setEducationInfo(false);
                break;
            case 'education':
                setEducationInfo(true);
                setContactInfo(false);
                break;
            default:
                break;
        }
    }

    return (
        <Fragment>
            <div className="col-4">
                <List component="nav">

                    {props.auth ? props.handle.handle === props.auth.username ? renderEdit() : '' : ''}

                    <ListItem button onClick={() => handleClick('contact')}>
                        <ListItemIcon>
                            <Icon>contacts</Icon>
                        </ListItemIcon>
                        <ListItemText primary="Contact Info" />
                    </ListItem>
                    <ListItem button onClick={() => handleClick('education')}>
                        <ListItemIcon>
                            <Icon>school</Icon>
                        </ListItemIcon>
                        <ListItemText primary="Work and Education" />
                    </ListItem> 
                </List>
            </div>
            <div className="col-8">
                {renderList()}
            </div>
        </Fragment>
    );
}

export default About;
