import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, GridList, GridListTile, GridListTileBar, IconButton, Icon } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    root: {
        backgroundColor: theme.palette.background.paper, 
        // width: '70%',

    },
    header: {
        margin: '20px 0px',
        textAlign: 'center',
    },
    gridList: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        // height: '100%',
        // transform: 'translateZ(0)',
    },
    titleBar: {
        background: 
        'linear-gradient(to bottom, rgba(0,0,0,0.7) 0%, ' +
        'rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    },
    icon: {
        color: 'white',
    },
    sorry: {

    },
}))

function Photos(props) {

    const classes = useStyles();

    function renderPhotos() {

        if(props.info.gallery.length >= 1) {
            return (
                props.info.gallery.map((photo, index) => {
                    return (
                        <div className={classes.root}>
                            <GridList cols={2} cellHeight={200} spacing={12} className={classes.gridList}>
                                <GridListTile key={index}>
                                    <img src={photo.img} alt={photo.title} />
                                    <GridListTileBar 
                                        title={photo.title}
                                        titlePosition="top"
                                        actionIcon={
                                            <IconButton className={classes.icon}>
                                                <Icon>star_border</Icon>
                                            </IconButton>
                                        }
                                        actionPosition="left"
                                        className={classes.titleBar}
                                    />
                                </GridListTile>
                            </GridList>
                        </div>
                    );
                })
            );
        } else {
            return (
                <div style={{zIndex: 1500}} className={classes.sorry}>
                    <Typography variant="h4" align="center" color="textSecondary" >
                        Sorry, no photos to show
                    </Typography>
                    <Typography variant="h4" align="center" color="textSecondary" >
                        {`¯|_(ツ)_/¯`}
                    </Typography>
                </div>
            );
        }
    }

    return (
        <Fragment>

        <Grid xs={12}>
            <Typography variant="h3" color="textSecondary" className={classes.header}>
                Gallery
            </Typography>
        </Grid>
        <Grid xs={12}>
            {renderPhotos()}
        </Grid>

        </Fragment>
    );
}

export default Photos;
