import React, { Fragment, useContext } from 'react';
import AuthContext from '../../../context/AuthContext';


import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import Icon from '@material-ui/core/Icon';
import Fab from '@material-ui/core/Fab';

import Moment from 'moment';

const useStyles = makeStyles(theme => ({
    root: {
        marginTop: '40px',
        color: 'black !important',
        // width: '60% !important',
        flexWrap: 'wrap',
        flexDirection: 'column',
    },
    label: {
        color: 'black !important',
    },
    postStory: {
        marginTop: '50px',
    },
    form: {
        display: 'flex',
        flexWrap: 'wrap',
        flexDirection: 'row',
    }
}))


function Story() {

    const [story, setStory] = React.useState('')

    const classes = useStyles();
    const auth = useContext(AuthContext)

    function handleInputChange(e) {
        const { value } = e.target
        setStory(value)
    }

    function handleStoryPost(e) {
        e.preventDefault();
        const username = auth.user.username;
        const time = Moment();
        fetch('/api/social/newStory', {
            method: 'POST',
            body: JSON.stringify({username: username, story: story, time: time}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((user) => {
            console.log(user);
            auth.updateAuth(user);
        })
    }

    return (
        <Fragment>
            <form className={classes.form} onSubmit={handleStoryPost}>
                <FormControl>
                    <TextField
                        className={classes.root}
                        multiline
                        fullWidth={false}
                        rowsMax="8"
                        label="Story"
                        helperText="Story here..."
                        name="setStory"
                        value={story}
                        onChange={handleInputChange}
                        InputLabelProps={{className: classes.label}}
                        inputProps={{className: classes.label}}
                    />
                    <Fab type="submit" size="small" className={classes.postStory} color="primary"><Icon>add</Icon></Fab>
                </FormControl>
            </form>
        </Fragment>
    );
}

export default Story;
