import React, { useContext, Fragment } from 'react';
import AuthContext from '../../../context/AuthContext';
import Moment from 'react-moment';

import { makeStyles } from '@material-ui/core/styles';
import { List, ListItem, ListItemText, ListItemAvatar, Divider, Typography, Avatar, Icon, Fab} from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    root: {
        backgroundColor: theme.palette.background.paper,
    },
}));

function Timeline() {

    const classes = useStyles();
    const auth = useContext(AuthContext);

    function handleDelete(id) {
        console.log(id);
        fetch('/api/social/deleteStory', {
            method: 'POST',
            body: JSON.stringify({id: id}),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((user) => {
            console.log(user);
            auth.updateAuth(user);
        })
    }

    return (
        <div>
            <List className={classes.root}>
                {auth.user ? auth.user.Stories.map((story, index) => (
                    <Fragment key={story._id}>
                        <ListItem>
                            <ListItemAvatar><Avatar alt={index} src="/imgs/avatar.jpg" /></ListItemAvatar>
                            <ListItemText 
                                primary={story.story} 
                                secondary={
                                    <Moment date={story.time} format={'dddd h:mm a'} />
                                }
                            >
                            </ListItemText>
                            <Fab onClick={() => handleDelete(story._id)} size="small">
                                <Icon>delete_outline</Icon>
                            </Fab>
                        </ListItem>
                        <Divider />
                    </Fragment>
                )) : ''}
            </List>
        </div>
    );
}

export default Timeline;
