import TokenStore from '../lib/TokenStore';

var username = ''

function handleLogin(user, authToken, state) {
    const newState = {...state}
    TokenStore.setToken(authToken);
    newState.user = user;
    newState.authToken = authToken;
    username = user;
  }

  function handleLogout(user, authToken, state) {
    TokenStore.clearToken();
    const newState = {...state}
    newState.user = undefined;  
    newState.authToken = undefined;
  }


const initialState = {
    username: '',
    user: undefined,
    authToken: TokenStore.getToken(),
    onLogin: handleLogin,
    onLogout: handleLogout,
};

const reducer = (state = initialState, action) => {
    const newState = {...state};
    console.log(action);
    if(action.type==="USER"){
        newState.username = action.val
    }

    return newState;
};

export default reducer;
