import React, { useContext, useEffect } from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import Avatar from '@material-ui/core/Avatar';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Icon from '@material-ui/core/Icon';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Moment from 'react-moment';
import FormatDate from 'moment';
import AuthContext from '../../context/AuthContext';
import { Redirect } from 'react-router-dom';

const drawerWidth = 240;


export default function PersistentDrawerLeft(props) {
  const useStyles = makeStyles(theme => ({
    root: {
      position: 'relative',
      display: 'flex',
    },
    appBar: {
      transition: theme.transitions.create(['margin', 'width'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
    },
    appBarShift: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
      transition: theme.transitions.create(['margin', 'width'], {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
    },
    toolBar: {
      display: 'flex',
      alignItems: 'center',
      flexWrap: 'nowrap',
      // justifyContent: 'center',
      // display: 'block',
      // textAlign: 'center',
      background: props.mood ? `${props.mood.toolbar}` : 'rgb(196, 196, 196)',
    },
    menuButton: {
        position: 'absolute',
        left: '3%',
        marginRight: theme.spacing(2),
        color: props.mood ? `${props.mood.menuButton}` : 'white',
    },
    barTitle: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      display: 'inline-block',
      color: props.mood ? `${props.mood.barTitle}` : 'white',
      width: '300px',
    },
    typo: {
      width: 'auto',
    },
    hide: {
      display: 'none',
    },
    drawer: {
      width: drawerWidth,
      flexShrink: 0,
    },
    drawerPaper: {
      width: drawerWidth,
    },
    drawerHeader: {
      display: 'flex',
      alignItems: 'center',
      padding: '0 8px',
      ...theme.mixins.toolbar,
      justifyContent: 'flex-end',
    },
    content: {
      flexGrow: 1,
      padding: theme.spacing(3),
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      marginLeft: -drawerWidth,
    },
    contentShift: {
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: 0,
    },
    moment: {
      position: 'absolute',
      right: '3%',
      marginRight: theme.spacing(2),
      display: 'flex',
      alignItems: 'center',
    },
    clock: {
      marginRight: '5px'
    }
  }));

  const classes = useStyles();
  const theme = useTheme();
  const value = useContext(AuthContext);
  console.log(value)

  const [open, setOpen] = React.useState(false);
  const [user, setUser] = React.useState({})


  function handleDrawerOpen() {
    setOpen(true);
  }

  function handleDrawerClose() {
    setOpen(false);
  }

  function handleMenuItem(item) {
    console.log('HANDLE MENU')
    if(item === 'Inbox'){return <Redirect to={'/inbox'} />}
    if(item === 'Settings'){return <Redirect to={'/settings'} />}
    if(item === 'Logout'){value.onLogout(); return <Redirect to={'/login'} />}
  }

  function getTime() {
    date = FormatDate()
  }
  var date = ''
  setInterval(getTime, 30000);

  return (
      <div>
        <div className={classes.root}>
          <CssBaseline />
          <AppBar
            position="relative"
            className={clsx(classes.appBar, {
              [classes.appBarShift]: open,
            })}
          >
            <Toolbar className={classes.toolBar}>
              <IconButton
                color="inherit"
                aria-label="Open drawer"
                onClick={handleDrawerOpen}
                edge="start"
                className={clsx(classes.menuButton, open && classes.hide)}
              >
                <Icon>menu</Icon>
              </IconButton>
              <div className={classes.barTitle}>
                <Avatar alt={value.user ? value.user.username : console.log('NOPE')} src='/imgs/avatar.jpg' />
                <Typography className={classes.typo} variant="h6">
                  {value.user ? value.user.username : console.log('NOPE')}
                </Typography>
              </div>
              <div className={classes.moment}>
                <Icon className={classes.clock}>access_time</Icon>
                <Moment interval={30000} date={date} format={'dddd h:mm a'} />
              </div>
            </Toolbar>
          </AppBar>
          <Drawer
            className={classes.drawer}
            variant="persistent"
            anchor="left"
            open={open}
            classes={{
              paper: classes.drawerPaper,
            }}
          >
            <div className={classes.drawerHeader}>
              <IconButton onClick={handleDrawerClose}>
                {theme.direction === 'ltr' ? <Icon>chevron_left</Icon> : <Icon>cheveron_right</Icon>}
              </IconButton>
            </div>
            <Divider />
            <List>
              {['Inbox', 'Settings', 'Logout'].map((text, index) => (
                <ListItem onClick={() => handleMenuItem(text)} button key={text}>
                  <ListItemIcon >{index % 2 === 0 ? <Icon>inbox</Icon> : <Icon>mail</Icon>}</ListItemIcon>
                  <ListItemText primary={text} />
                </ListItem>
              ))}
            </List>
            <Divider />
            </Drawer>
        </div>
      </div>
  );
}
