import React, { useState, Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Paper, TextField, Button, Snackbar, SnackbarContent, IconButton, Icon, Typography } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    textFields: {
        marginTop: '20px',
        width: '80%',
    },
    paper: {
        width: '80%',
        padding: '20px'
    },
    form : {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'nowrap',
        margin: '50px 0px'
    },
    button: {
        padding: '10px',
        marginTop: '20px'
    },
    snackbar: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        marginRight: '10px'
    }
}))

function ContactForm() {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [message, setMessage] = useState('');
    const [open, setOpen] = useState(true);

    const classes = useStyles();

    function handleSubmit(e) {
        e.preventDefault();
        console.log(name, email, phone, message);
        const data = {name: name, email: email, phone: phone, message: message};
        fetch('/api/contact/newContact', {
            method: 'POST',
            body: JSON.stringify(data),
            headers: {'Content-Type': 'application/json'}
        }).then(res => res.json())
        .then((response) => {
            console.log(response);
            setOpen(true)
        });
    };

    function handleClose() {
        setOpen(false);
    }

    return (
        <div className={classes.form}>
            <Paper  className={classes.paper}>
                <form onSubmit={handleSubmit}>
                    <TextField 
                        variant="outlined"
                        label="Your Name"
                        name="Name"
                        required
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        className={classes.textFields}
                    />
                    <TextField 
                        variant="outlined"
                        label="Your Email"
                        name="Email"
                        type="email"
                        required
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className={classes.textFields}
                    />
                    <TextField 
                        variant="outlined"
                        label="Your Phone Number"
                        name="Phone"
                        required
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        className={classes.textFields}
                    />
                    <TextField 
                        variant="outlined"
                        label="Message"
                        name="Message"
                        required
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        className={classes.textFields}
                    />
                    <Button type="submit" className={classes.button} fullWidth color='secondary' >Send</Button>
                </form>

                <Snackbar
                    anchorOrigin={{vertical: 'bottom', horizontal: 'center'}}
                    open={open}
                    // autoHideDuration={6000}
                    onClose={handleClose}
                >

                    <SnackbarContent 
                        message={
                            <Fragment>
                                <Icon>check</Icon>
                                <Typography>
                                    "Your contact information has been saved!"
                                </Typography>
                            </Fragment>
                        }
                        action={
                            <IconButton onClick={handleClose}>
                                <Icon>close</Icon>
                            </IconButton>
                        }
                        classes={{message: classes.snackbar}}
                    />

                </Snackbar>

            </Paper>
        </div>
    );
}

export default ContactForm;
