import React, { Fragment } from 'react';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import Icon from '@material-ui/core/Icon';
import Fab from '@material-ui/core/Fab';
import { InputLabel, Input, Button } from '@material-ui/core';


const useStyles = makeStyles(theme => ({
    root: {
        marginTop: '40px',
        color: 'black !important',
        width: '60% !important',
    },
    label: {
        color: 'white !important',
    },
    underline: {
        // '&:hover': {
        //     borderBottom: '2px solid rgb(208, 109, 255) !important',
        // },
        '&:hover': {
            '&:before': {
                borderBottom: '2px solid rgb(208, 109, 255) !important',
            },
            '&:after': {
                borderBottom: '2px solid rgb(208, 109, 255) !important',
            },
            borderBottom: '1px solid rgb(182, 25, 255) !important',
        },
        '&:hover:not($disabled):not($focused):not($error):after': {
            borderBottom: '2px solid rgb(208, 109, 255) !important'
        }

    },
    postStory: {
        display: 'block',
        margin: '20px auto',
        padding: '5px 10px',
    },
    form: {
        // display: 'flex',
        // flexWrap: 'wrap',
        // flexDirection: 'row',
        width: '50%',
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        background: 'rgb(0, 0, 0, 0.08)',
        padding: '20px',
    },
}))


function RegisterForm() {

    const [values, setValues] = React.useState({
        username: '',
        email: '',
        password: '',
    })

    const classes = useStyles();

    function handleInputChange(e) {
        const { name, value } = e.target;
        setValues({[name]: value})
    }

    function handleOnSubmit(e) {
        e.preventDefault();
        console.log(values.username, values.email, values.password)
        fetch('/api/users/register', {
            method: 'POST',
            body: JSON.stringify({username: values.username, email: values.email, password: values.password}),
            headers: {'Content-Type': 'application/json'}
        })
        .then(res => res.json())
        .then((result) => {console.log(result)});
    }

    return (
        <Fragment>
            <form className={classes.form} onSubmit={handleOnSubmit}>
                    <FormControl className={classes.root}>
                        <InputLabel className={classes.label}>Username</InputLabel>
                        <Input 
                            inputProps={{className: classes.input}}
                            name='username'
                            value={values.username}
                            type='text'
                            onChange={handleInputChange}
                        />
                    </FormControl>
                    <FormControl className={classes.root}>
                        <InputLabel className={classes.label}>Email</InputLabel>
                        <Input 
                            inputProps={{className: classes.input}}
                            name='email'
                            value={values.email}
                            type="email"
                            onChange={handleInputChange}
                        />
                    </FormControl>
                    <FormControl className={classes.root}>
                        <InputLabel className={classes.label}>Password</InputLabel>
                        <Input 
                            classes={{underline: classes.underline}}
                            name='password'
                            value={values.password}
                            type="password"
                            onChange={handleInputChange}
                        />
                    </FormControl>
                        <Fab 
                            size="small" 
                            variant="extended"
                            color="primary"
                            className={classes.postStory} 
                            type="submit"
                        >Register</Fab>

            </form>
        </Fragment>
    );
}

export default RegisterForm;
