import React, { Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Icon } from '@material-ui/core';

function SideBar(props) {

    const { chats, activeChat, setActiveChat, logout, user} = props;

	console.log(user);

    return (
        <Fragment>
            <div id="side-bar">
					<div className="heading">
						<div className="app-name">Our Cool Chat <Icon>chevron_down</Icon></div>
						<div className="menu">
							<Icon>menu</Icon>
						</div>
					</div>
					<div className="search">
						<i className="search-icon"><Icon>search</Icon></i>
						<input placeholder="Search" type="text"/>
						<div className="plus"></div>
					</div>
					<div 
						className="users" 
						ref='users' 
						onClick={(e)=>{ (e.target === user) && setActiveChat(null) }}>

						{
						chats.map((chat)=>{
							if(chat.name){
								const lastMessage = chat.messages[chat.messages.length - 1];
								const user = chat.users.find(({name})=>{
									return name !== this.props.name
								}) || { name:"Community" }
								const classNames = (activeChat && activeChat.id === chat.id) ? 'active' : ''

								return(
								<div 
									key={chat.id} 
									className={`user ${classNames}`}
									onClick={ ()=>{ setActiveChat(chat) } }
									>
									<div className="user-photo">{user.name[0].toUpperCase()}</div>
									<div className="user-info">
										<div className="name">{user.name}</div>
										{lastMessage && <div className="last-message">{lastMessage.message}</div>}
									</div>

								</div>
							)
							}

							return null
						})	
						}

					</div>
					<div className="current-user">
						<span>{user.name}</span>
						<div onClick={()=>{logout()}} title="Logout" className="logout">
							<Icon>eject</Icon>	
						</div>
					</div>
			</div>
        </Fragment>
    );
}

export default SideBar;
